import {
    updateEmail,
    updateProfile,
    updatePassword,
    createUserWithEmailAndPassword,
    signInWithEmailAndPassword,
} from "firebase/auth";
import { useState } from 'react';
import { BrowserRouter, Route, Switch, Redirect, withRouter } from 'react-router-dom';
import { auth } from '../firebase/firebase';
import AuthMsg from '../components/AuthMsg';
import SignIn from '../components/SignIn';
import SignUp from '../components/SignUp';
import Nav from '../components/Nav';
import { NavMediaBar, Profile, SignInBtn, SignOutBtn } from '../components/Nav_components/index';
import films from '../assets/films/index';
import series from '../assets/series/index';
import Home from '../components/Home';
import Dashboard from '../components/Dashboard';
import Account from '../components/Account';
import Footer from '../components/Footer';
import { customMsg } from '../helper/helper';

const Router = (props) => {
    const [mediaType, setMediaType] = useState({ films }); //films default
    const [MTKey, setMTKey] = useState(Object.keys({ films }));

    const [authState, setAuthState] = useState(localStorage.getItem('authState')); //account params
    const [displayName, setDN] = useState();
    const [email, setEmail] = useState(null);

    const [signInMsg, setSIMsg] = useState(null);
    const [signUpMsg, setSUMsg] = useState(null);
    const [acctMsg, setAcctMsg] = useState(null);

    auth.onAuthStateChanged(authUser => {
        authUser
            ? localStorage.setItem('authState', JSON.stringify(authUser))
            : localStorage.removeItem('authState');
    });

    async function saveUserName(authObj, name) {
        updateProfile(authObj.currentUser, {
            displayName: `${name}`,
        }).then(() => {
            console.log('username saved');
            // ...
        }).catch((error) => {
            // An error occurred
            // ...
            console.log(error, 'error updating profile');
        });
    }

    async function saveUserEmail(authObj, email) {
        updateEmail(authObj.currentUser, `${email}`).then(() => {
            // Email updated!
        }).catch((error) => {
            // An error occurred
            console.log(error, 'error updating email')
        });
    }

    async function saveUserPassword(authObj, pw) {
        updatePassword(authObj.currentUser, `${pw}`).then(() => {
            console.log('pw updated')
            // Update successful.
        }).catch((error) => {
            // An error ocurred
            console.log(error, 'error updating pw')
        });
    }

    function PrivateRoute({ children, ...rest }) {
        return (
            <Route {...rest} render={() => {
                return authState !== null
                    ? children
                    : <Redirect to='/' />
            }} />
        )
    }

    function RouteWrap({ children, ...rest }) {
        return (
            <Route {...rest} render={() => {
                return authState === null
                    ? children
                    : <Redirect to='/dashboard' />
            }} />
        );
    }

    async function createUser(email, password, name) {
        createUserWithEmailAndPassword(auth, email, password)
            .then((userCredential) => {
                //const user = userCredential.user;
                //console.log(user, 'user/createdUser');
                //setSUMsg(<AuthMsg useClass={'auth-success'} msg={customMsg('auth/sign-up-success')} />);
            })
            .catch((error) => {
                const errorCode = error.code;
                //const errorMessage = error.message;
                //console.log('error occured', errorCode, errorMessage);
                setSUMsg(<AuthMsg useClass={`auth-error`} msg={customMsg(errorCode)} />)
            });
    }

    async function signInUser(email, password) {
        signInWithEmailAndPassword(auth, email, password)
            .then((userCredential) => {
                //console.log('sign in successful', userCredential.user);
                //setSIMsg(<AuthMsg useClass={`auth-success`} msg={customMsg('auth/sign-in-success')} />);
            })
            .catch((error) => {
                const errorCode = error.code;
                //const errorMessage = error.message;
                //console.log('sign in error', error, errorCode, errorMessage);
                setSIMsg(<AuthMsg useClass={`auth-error`} msg={customMsg(errorCode)} />);
            });
    }

    return (
        <BrowserRouter>
            <Switch>
                <RouteWrap >
                    <Nav
                        SignInBtn={<SignInBtn />}
                    />
                    <Home setEmail={setEmail} />
                </RouteWrap>

                <PrivateRoute exact path='/dashboard'>
                    <Nav
                        NavMediaBar={<NavMediaBar setMediaType={setMediaType} setMTKey={setMTKey} films={films} series={series} />}
                        Profile={<Profile setAuthState={setAuthState} email={email} />}
                    />
                    <Dashboard mediaType={mediaType} MTKey={MTKey} />
                </PrivateRoute>
                <PrivateRoute exact path='/account'>
                    <Nav />
                    <Account
                        saveUserName={saveUserName}
                        saveUserEmail={saveUserEmail}
                        saveUserPassword={saveUserPassword}
                        displayName={displayName}
                        email={email}
                        acctMsg={acctMsg}
                        setAcctMsg={setAcctMsg}
                    />
                </PrivateRoute>
                <Route exact path='/signin'>
                    {authState === null ?
                        <div>
                            <Nav />
                            <SignIn
                                signInUser={signInUser}
                                signInMsg={signInMsg}
                                setSIMsg={setSIMsg}
                                authState={authState}
                            />
                        </div>
                        : <Redirect to='/dashboard' />
                    }
                </Route>
                <Route exact path='/signup'>
                    {authState === null ?
                        <div>
                            <Nav />
                            <SignUp
                                email={email}
                                createUser={createUser}
                                signUpMsg={signUpMsg}
                                setSUMsg={setSUMsg}
                                authState={authState}
                            />
                        </div>
                        : <Redirect to='/dashboard' />
                    }
                </Route>
            </Switch>
            <Footer />
        </BrowserRouter >
    );
};

export default withRouter(Router);


