import { useRef, useEffect } from 'react';
import { Link } from "react-router-dom";
//import SignInBtn from './Nav_components/SignInBtn';
import NavMediaBar from './Nav_components/NavMediaBar';
import Profile from './Nav_components/Profile';
import NetflixLogo from '../images/Netflix-Logo.png';
import '../styles/Nav.css';

const Nav = (props) => {
    const { email, setMediaType, setMTKey, films, series, showProfile, SignInBtn, NavMediaBar, } = props;
    console.log(SignInBtn);
    const navBar = useRef();

    useEffect(() => {
        document.addEventListener('scroll', transparentScroll);
        return () => document.removeEventListener('scroll', transparentScroll);
    });

    function transparentScroll() {
        if (window.scrollY === 0) {
            navBar.current.classList.add('transparent');
        } else if (window.scrollY !== 0) {
            navBar.current.classList.remove('transparent');
        }
    }

    return (
        <nav ref={navBar} className='navbar transparent'>
            <div className='nav-container'>
                <div className='left-align'>
                    <div className='logoContainer'>
                        <Link to='./'><img className='logoImg' src={NetflixLogo} alt='NetflixLogo' /></Link>
                    </div>
                    {(films && series) ? <NavMediaBar setMediaType={setMediaType} setMTKey={setMTKey} films={films} series={series} /> : null}
                </div>
                {SignInBtn()}
                {Profile()}
                {showProfile ? <Profile setAuthState={props.setAuthState} email={email} /> : null}
            </div>
        </nav>
    );
}

export default Nav;
