import { useRef, useState } from 'react';
import { auth } from '../firebase/firebase';
import { useHistory } from 'react-router-dom';
import { customMsg, checkPwMatch } from '../helper/helper';
import {
    getAuth,
    updateEmail,
    updateProfile,
    updatePassword,
    createUserWithEmailAndPassword,
    signInWithEmailAndPassword,
    onAuthStateChanged,
    reauthenticateWithCredential,
    EmailAuthProvider,
} from "firebase/auth";
import AuthMsg from './AuthMsg';
import '../styles/Account.css';

const Account = ({ displayName, email, acctMsg, setAcctMsg }) => {
    const nameField = useRef();
    const emailField = useRef();
    const currentEmail = useRef();
    const currentPw = useRef();
    const pw = useRef();
    const confirmpw = useRef();

    const [error, setError] = useState(null);
    const history = useHistory();

    function redirectSignUp() {
        history.push(`dashboard`);
    }

    async function handleSubmit(e) {
        e.preventDefault();
        // if (checkPwMatch(pw.current.value, confirmpw.current.value) === false) {
        //     setAcctMsg(<AuthMsg useClass={`auth-error`} msg={customMsg(`auth/confirm-pw-error`)} />);
        //     return;
        // }
        //saveUserName(auth, nameField.current.value);


        const auth = getAuth();
        const user = auth.currentUser;

        const credential = EmailAuthProvider.credential(
            currentEmail.current.value,
            currentPw.current.value,
        );

        // TODO(you): prompt the user to re-provide their sign-in credentials
        //console.log(credential);

        reauthenticateWithCredential(user, (currentEmail.current.value, currentPw.current.value)).then(() => {
            // User re-authenticated.
        }).catch((error) => {
            // An error ocurred
            // ...
            alert(error);
        });


        // reauthenticateWithCredential(auth.currentUser, credential).then(() => {
        //     console.log('reauthed');
        //     // User re-authenticated.
        // }).catch((error) => {
        //     // An error ocurred
        //     setError(<AuthMsg className={`error`} msg={error.message} />);
        // });


        // const promises = [];
        // promises.push(updateEmail(auth.currentUser, emailField.current.value));
        // promises.push(updatePassword(auth.currentUser, pw.current.value));
        // Promise.all(promises)
        //     .then(() => {
        //         history.push('/dashboard')
        //     })
        //     .catch((error) => {
        //         alert(error);
        //     })
        //     .finally(() => {
        //     })
        //await saveUserEmail();
        //await saveUserPassword(auth, );
        //redirectSignUp();
        //window.location.reload();
    }
    return (
        <div className='acct'>
            <div className='acct-container'>
                <h1>Update Account Info</h1>
                {error}
                <form onSubmit={(e) => handleSubmit(e)}>
                    <label>Current Email</label>
                    <input ref={currentEmail} type='email' placeholder='Email' defaultValue={email} required></input>

                    <label>Current Password</label>
                    <input ref={currentPw} type='password' placeholder='Current Password' required></input>

                    <label>New Email</label>
                    <input ref={emailField} type='email' placeholder='Email' required></input>

                    <label>New Password</label>
                    <input ref={pw} type='password' minLength='6' placeholder='Leave blank if not updating'></input>

                    <label>Confirm New Password</label>
                    <input ref={confirmpw} type='password' minLength='6'></input>

                    <input id='acct-submit' type='submit' value='Submit'></input>
                </form>
            </div>
            <div className='overlay'></div>
        </div >
    );
};

export default Account;

