import { Link } from 'react-router-dom';
import { useRef } from 'react';
import { auth } from '../firebase/firebase';
import { customMsg } from '../helper/helper';
import { SUError } from './AuthMsgs';
import '../styles/SignUp.css';

const SignUp = (props) => {
    const { createUser, signUpMsg, setSUMsg, email } = props;
    const name = useRef();
    const password = useRef();
    const confirmpw = useRef();
    const mail = useRef();

    console.log(auth.currentUser, 'currentUser1');

    function handleSubmit() {
        if (!name.current.value) {
            setSUMsg(<SUError msg={customMsg(`auth/no-name`)} />);
            return;
        }
        if (checkPwMatch(password.current.value, confirmpw.current.value) === false) {
            //console.log('passwords do not match');
            setSUMsg(<SUError msg={customMsg(`auth/confirm-pw-error`)} />);
            return;
        }
        createUser(mail.current.value, password.current.value, name);
    }

    function checkPwMatch(pw1, pw2) {
        return pw1 === pw2 ? true : false;
    }

    return (
        <div className='signUp'>
            <div className='signUp-overlay'>
                <div className='signUp-container'>
                    <h1>Sign Up</h1>
                    <div className='signUp-form'>
                        {signUpMsg}
                        <input ref={name} placeholder='First Name' />
                        <input ref={mail} type='email' placeholder='Email Address' defaultValue={email} />
                        <input ref={password} type='password' placeholder='Password' />
                        <input ref={confirmpw} type='password' placeholder='Confirm Password' />
                        <input id='signUp-submit' type='button' value='Sign Up' onClick={() => handleSubmit()} />
                    </div>
                    <h2>
                        Already a user? <Link to='/signin'>Sign in now</Link>
                    </h2>
                    <h3>
                        This page is protected by Google reCAPTCHA to ensure you're not a bot. Learn more.
                    </h3>
                </div>
            </div>
        </div>
    )
};

export default SignUp;
