        function makeStars(len) {
            let str = '';
            for (let i = 0; i < len; i++) {
                str += '*';
            }
            return str;
        }
    };

    async function submitHandSignIn(e) {
        e.preventDefault();
        console.log('prevented Submit');
    }


    return (
        <BrowserRouter>
            <Nav location={props.location} />
            <Switch>
                <Route exact path='/signin' render={() => <SignIn submitHandSignIn={submitHandSignIn} location={props.location} />} />
                <Route exact path='/signup' component={SignUp} />
                <Route exact path='/' render={() => <Homes />} />
            </Switch>
        </BrowserRouter >
    );
};

export default withRouter(Router);

