import { useRef, useMemo, createRef, useState } from 'react';
import { capitalizeFirstLetter } from '../helper/helper';
import YoutubeEmbed from './YoutubeEmbed';
import MainMedia from './MainMedia';
import MediaBar from './MediaBar';
import films from '../assets/films';
import joker from '../images/joker.jpg';
import '../styles/Dashboard.css';

const Dashboard = () => {
    //document.querySelector('html').style.backgroundImage = `url(${joker})`;
    const [currentMedia, setCurrentMedia] = useState(null);
    const [y, setY] = useState(null);
    const [z, setZ] = useState(null);

    function onClickHand(e) {
        const [genre, ,] = e.target.classList;
        const filmName = e.target.alt;
        const x = document.getElementById(`${genre}Preview`);
        const catalog = films[genre].find((item) => item.title === filmName);
        setCurrentMedia(genre);
        setY(<YoutubeEmbed embedId={catalog.YTLink} />);
        //x.append(<YoutubeEmbed embedId={'NE8tlDeSLmk'} />)


        // const elem = getPreviewBar(genre);
        // const descript = getDescript(genre, filmName);
        // const img = require(`../assets/films/${genre}/${filmName}/large.jpg`);
        // elem.style.backgroundImage = `url(${img.default})`;
        // elem.textContent = descript;


        function getPreviewBar(genre) {
            return document.getElementById(genre);
        }

        function getDescript(genre, filmName) {
            return films[genre].find((item) => {
                return item.title === filmName;
            }).description;
        }

    }

    function hoverPlayBtn(e) {
        e.target.classList.add('hover');
    }

    function hoverOffPlayBtn(e) {
        e.target.classList.remove('hover');
    }

    function playBtnClickHand(e, url) {
        setZ(<YoutubeEmbed embedId={url} stopPlayHand={stopPlayHand} />);
    }

    function stopPlayHand(e) {
        setZ(null);
    }

    function makeMediaBar() {
        let arr = [];
        for (let key in films) {
            arr.push(
                <div>
                    <div className='mediaTitle'>{capitalizeFirstLetter(key)}</div>
                    <MediaBar
                        title={key}
                        onClickHand={onClickHand}
                        hoverHand={hoverHand}
                        mouseOutHand={mouseOutHand}
                    />
                    <div id={`${key}Preview`} className='bigPreview'>
                        {currentMedia === key ? y : null}
                    </div>
                </div>
            );
        }
        return arr.map((item, index) => {
            return (
                <div key={index}>
                    {item}
                </div>
            );
        });
    }

    async function playVideo(e) {
        console.log(e.target.parentNode.style);
        const url = 'https://api.themoviedb.org/3/movie/550?api_key=6570a26341e47014d7ae8da875015c6d&language=en-US';
        const x = await fetch(url);
        const y = await x.text();
        console.log(y);
    }

    function hoverHand(e) {
        e.target.classList.add('cardImgHover');
    }

    function mouseOutHand(e) {
        e.target.classList.remove('cardImgHover');
    }

    //style={{ background: `url(${joker})` }}

    return (
        <div className='dashboard'>
            <MainMedia
                display={`joker`}
                hoverPlayBtn={hoverPlayBtn}
                hoverOffPlayBtn={hoverOffPlayBtn}
                playBtnClickHand={playBtnClickHand} />
            <div className='media'>
                {makeMediaBar()}
            </div>
            {z}
        </div >
    );
};

export default Dashboard;


