import { Link, Switch } from 'react-router-dom';
import { useRef } from 'react';
import { getAuth, createUserWithEmailAndPassword } from "firebase/auth";
import { collection, addDoc, getDocs } from 'firebase/firestore';
import { db, users } from '../firebase/firebase';
import '../styles/SignUp.css';

const SignUp = (props) => {
    const { email } = props;

    const pw1 = useRef();
    const pw2 = useRef();
    const mail = useRef();

    async function handleSubmit(e) {
        e.preventDefault();
        const [firstName, email, pw1, pw2] = e.target.childNodes;
        if (checkPw(pw1, pw2) === false) {
            console.log('passwords do not match');
            return;
        }
        authUser(email, pw1);
        //check if email in database already
        // setCursor('wait');
        // console.log('pw match');
        // await addToDoc(firstName.value, email.value, pw1.value);
        // console.log('uploaded');
        // setTimeout(() => {
        //     setCursor('default');
        // }, 2000);

    }

    function checkPw(pw1, pw2) {
        return pw1.value === pw2.value ? true : false;
    }

    function setCursor(val) {
        document.querySelectorAll('*').forEach((elem) => {
            elem.style.cursor = val;
        });
    }

    async function addToDoc(firstName, email, password) {
        await addDoc(collection(db, 'users'), {
            firstName,
            email,
            password,
        });
    }

    async function authUser(email, password) {
        console.log('in fxn')
        const auth = getAuth();
        createUserWithEmailAndPassword(auth, email, password)
            .then((userCredential) => {
                // Signed in 
                console.log(userCredential);
                const user = userCredential.user;
                // ...
            })
            .catch((error) => {
                const errorCode = error.code;
                const errorMessage = error.message;
                console.log(errorCode, errorMessage);
                // ..
            });
    }

    return (
        <div className='signUp' onSubmit={(e) => handleSubmit(e)} style={{ cursor: 'default' }}>
            <input placeholder='First Name' />
            <input type='email' placeholder='Email Address' defaultValue={email} />
            <input type='password' placeholder='Password' />
            <input type='password' placeholder='Confirm Password' />
            <input type='submit' value='Sign Up' />
            <div>
                Already a user? <Link to='/signin'>Sign in now</Link>
            </div>
        </div>
    )
};

export default SignUp;
