import { Link, Redirect, Switch } from 'react-router-dom';
import { useRef, useState } from 'react';
import { createUserWithEmailAndPassword, signInWithEmailAndPassword, onAuthStateChanged } from "firebase/auth";
import { collection, addDoc, getDocs } from 'firebase/firestore';
import { db, auth } from '../firebase/firebase';
import { customMsg } from '../helper/helper';
import { SUError, SUSuccess } from './AuthMsgs';
import '../styles/SignUp.css';

const SignUp = (props) => {
    const { createUser, setAuthState, signUpMsg, setSUMsg, email } = props;
    // onAuthStateChanged(auth, (user) => {
    //     if (user) {
    //         // User is signed in, see docs for a list of available properties
    //         // https://firebase.google.com/docs/reference/js/firebase.User
    //         const uid = user.uid;
    //         //console.log(auth.currentUser, 'currentUser');
    //         setSignedIn(auth.currentUser);
    //         // ...
    //     } else {
    //         // User is signed out
    //         // ...
    //     }
    // });
    const name = useRef();
    const password = useRef();
    const confirmpw = useRef();
    const mail = useRef();

    console.log(auth.currentUser, 'currentUser1');

    function handleSubmit() {
        //console.log(pw1.current.value, pw2.current.value, mail.current.value);
        //const [firstName, email, pw1, pw2] = e.target.childNodes;
        if (checkPw(password.current.value, confirmpw.current.value) === false) {
            //console.log('passwords do not match');
            setSUMsg(<SUError msg={customMsg(`auth/confirm-pw-error`)} />);
            return;
        }
        createUser(mail.current.value, password.current.value);

        //check if email in database already
        // setCursor('wait');
        // console.log('pw match');
        // await addToDoc(firstName.value, email.value, pw1.value);
        // console.log('uploaded');
        // setTimeout(() => {
        //     setCursor('default');
        // }, 2000);
    }

    function checkPw(pw1, pw2) {
        return pw1 === pw2 ? true : false;
    }

    // function setCursor(val) {
    //     document.querySelectorAll('*').forEach((elem) => {
    //         elem.style.cursor = val;
    //     });
    // }

    // async function addToDoc(firstName, email, password) {
    //     await addDoc(collection(db, 'users'), {
    //         firstName,
    //         email,
    //         password,
    //     });
    // }



    return (
        <div className='signUp'>
            <div className='signUp-overlay'>
                <div className='signUp-container'>
                    <h1>Sign Up</h1>
                    <div className='signUp-form'>
                        {signUpMsg}
                        <input placeholder='First Name' />
                        <input ref={mail} type='email' placeholder='Email Address' defaultValue={email} />
                        <input ref={pw1} type='password' placeholder='Password' />
                        <input ref={pw2} type='password' placeholder='Confirm Password' />
                        <input id='signUp-submit' type='button' value='Sign Up' onClick={() => handleSubmit()} />
                    </div>
                    <h2>
                        Already a user? <Link to='/signin'>Sign in now</Link>
                    </h2>
                    <h3>
                        This page is protected by Google reCAPTCHA to ensure you're not a bot. Learn more.
                    </h3>
                </div>
            </div>
        </div>
    )
};

export default SignUp;
