import { useRef, useState } from 'react';
import { auth } from '../firebase/firebase';
import { useHistory } from 'react-router-dom';
import { customMsg, checkPwMatch } from '../helper/helper';
import {
    updateEmail,
    updateProfile,
    updatePassword,
    createUserWithEmailAndPassword,
    signInWithEmailAndPassword,
    onAuthStateChanged,
    reauthenticateWithCredential,
} from "firebase/auth";
import AuthMsg from './AuthMsg';
import '../styles/Account.css';

const Account = ({ displayName, email, acctMsg, setAcctMsg }) => {
    const nameField = useRef();
    const emailField = useRef();
    const currentpw = useRef();
    const pw = useRef();
    const confirmpw = useRef();

    const [error, setError] = useState(null);
    const history = useHistory();

    function redirectSignUp() {
        history.push(`dashboard`);
    }

    async function handleSubmit(e) {
        e.preventDefault();
        // if (checkPwMatch(pw.current.value, confirmpw.current.value) === false) {
        //     setAcctMsg(<AuthMsg useClass={`auth-error`} msg={customMsg(`auth/confirm-pw-error`)} />);
        //     return;
        // }
        //saveUserName(auth, nameField.current.value);
        reauthenticateWithCredential(auth.currentUser, currentpw.current.value).then(() => {
            console.log('reauthed');
            // User re-authenticated.
        }).catch((error) => {
            // An error ocurred
            setError(<AuthMsg className={`error`} msg={error.message} />);
        });
        // const promises = [];
        // promises.push(updateEmail(auth.currentUser, emailField.current.value));
        // promises.push(updatePassword(auth.currentUser, pw.current.value));
        // Promise.all(promises)
        //     .then(() => {
        //         history.push('/dashboard')
        //     })
        //     .catch((error) => {
        //         alert(error);
        //     })
        //     .finally(() => {
        //     })
        //await saveUserEmail();
        //await saveUserPassword(auth, );
        //redirectSignUp();
        //window.location.reload();
    }
    return (
        <div className='acct'>
            <div className='acct-container'>
                <h1>Update Account Info</h1>
                {error}
                <form onSubmit={(e) => handleSubmit(e)}>
                    <label>First Name</label>
                    <input ref={nameField} type='text' placeholder='Name' defaultValue={displayName} required></input>

                    <label>Email</label>
                    <input ref={emailField} type='email' placeholder='Email' defaultValue={email} required></input>

                    <label>Current Password</label>
                    <input ref={currentpw} type='password' placeholder='Current Password' required></input>

                    <label>New Password</label>
                    <input ref={pw} type='password' minLength='6' placeholder='Leave blank if not updating'></input>

                    <label>Confirm New Password</label>
                    <input ref={confirmpw} type='password' minLength='6'></input>

                    <input id='acct-submit' type='submit' value='Submit'></input>
                </form>
            </div>
            <div className='overlay'></div>
        </div >
    );
};

export default Account;
