import { useRef } from 'react';
import { Link } from 'react-router-dom';
import { auth } from '../firebase/firebase';
import { checkPwMatch } from '../helper/helper';
import AuthMsg from './AuthMsg';
import '../styles/Account.css';

const Account = ({ displayName, email }) => {
    const pw = useRef();
    const confirmpw = useRef();

    function handleSubmit(e) {
        e.preventDefault();
        if (checkPwMatch(pw.current.value, confirmpw.current.value) === false) {
            setSUMsg(<AuthMsg useClass={`auth-error`} msg={customMsg(`auth/confirm-pw-error`)} />);
            return;
        }
    }
    return (
        <div className='acct'>
            <div className='acct-container'>
                <h1>Update Account Info</h1>
                <form onSubmit={(e) => handleSubmit(e)}>
                    <label>First Name</label>
                    <input type='text' placeholder='Name' defaultValue={displayName} required></input>
                    <label>Email</label>
                    <input type='email' placeholder='Email' defaultValue={email} required></input>
                    <label>Password</label>
                    <input ref={pw} type='password' minLength='6' placeholder='Password' required></input>
                    <label>Confirm Password</label>
                    <input ref={confirmpw} type='password' minLength='6' placeholder='Confirm Password' required></input>
                    <input id='acct-submit' type='submit' value='Submit'></input>
                </form>
            </div>
            <div className='overlay'></div>
        </div >
    );
};

export default Account;
