import { useState } from 'react';
import { BrowserRouter, Route, Switch, Redirect, withRouter } from 'react-router-dom';
import { doc, setDoc } from "firebase/firestore";
import { createUserWithEmailAndPassword, signInWithEmailAndPassword, onAuthStateChanged } from "firebase/auth";
import { db, auth } from '../firebase/firebase';
import { SIError, SISuccess, SUError, SUSuccess } from '../components/AuthMsgs';
import SignIn from '../components/SignIn';
import SignUp from '../components/SignUp';
import Nav from '../components/Nav';
import films from '../assets/films/index';
import series from '../assets/series/index';
import Home from '../components/Home';
import Dashboard from '../components/Dashboard';
import Account from '../components/Account';
import Footer from '../components/Footer';
import { customMsg } from '../helper/helper';

const Router = (prop3000s) => {
    const [mediaType, setMediaType] = useState({ films });
    const [MTKey, setMTKey] = useState(Object.keys({ films }));
    const [authState, setAuthState] = useState(localStorage.getItem('authState'));
    const [email, setEmail] = useState(null);
    const [name, setName] = useState(null);
    const [signInMsg, setSIMsg] = useState(null);
    const [signUpMsg, setSUMsg] = useState(null);

    //console.log(authState, 'authstate');
    //console.log(mediaType, MTKey, 'router');

    //localStorage.clear();

    onAuthStateChanged(auth, (user) => {
        if (user) {
            // User is signed in, see docs for a list of available properties
            // https://firebase.google.com/docs/reference/js/firebase.User

            const uid = user.uid;
            saveUserInfo(uid, name);
            console.log('authed, router.js');
            setAuthState(true);
            localStorage.setItem('authState', true);
        } else {
            // User is signed out
        }
    });

    async function saveUserInfo(uid, name) {
        console.log(name);
        await setDoc(doc(db, 'netflix-users', `${uid}`), {
            name
        });
    }

    function PrivateRoute({ children, ...rest }) {
        console.log('children:', children, 'rest:', rest, authState);
        console.log(localStorage.getItem('authState'));
        return (
            <Route {...rest} render={() => {
                return authState !== null
                    ? children
                    : <Redirect to='/' />
            }} />
        )
    }

    async function createUser(email, password, name) {
        createUserWithEmailAndPassword(auth, email, password)
            .then((userCredential) => {
                const user = userCredential.user;
                console.log(user, 'user/createdUser');
                setName(name);
                setSUMsg(<SUSuccess msg={customMsg('auth/sign-up-success')} />);
            })
            .catch((error) => {
                const errorCode = error.code;
                const errorMessage = error.message;
                console.log('error occured', errorCode, errorMessage);
                setSUMsg(<SUError msg={customMsg(errorCode)} />)
            });

    }

    async function signInUser(email, password) {
        signInWithEmailAndPassword(auth, email, password)
            .then((userCredential) => {
                console.log('sign in successful', userCredential.user);
                setSIMsg(<SISuccess msg={customMsg('auth/sign-in-success')} />);
            })
            .catch((error) => {
                const errorCode = error.code;
                const errorMessage = error.message;
                console.log('sign in error', error, errorCode, errorMessage);
                setSIMsg(<SIError msg={customMsg(errorCode)} />);
            });
    }

    return (
        <BrowserRouter>
            <Switch>
                <Route exact path='/'>
                    {authState === null ?
                        (
                            <div>
                                <Nav setEmail={setEmail} showSignInBtn={true} />
                                <Home email={email} setEmail={setEmail} />
                                <Footer />
                            </div>
                        ) :
                        <Redirect to='/dashboard' />
                    }
                </Route>
                <PrivateRoute exact path='/dashboard'>
                    <Nav
                        setEmail={setEmail}
                        setAuthState={setAuthState}
                        setMediaType={setMediaType}
                        setMTKey={setMTKey}
                        films={films}
                        series={series}
                        showNavMB={true}
                        showProfile={true}
                    />
                    <Dashboard mediaType={mediaType} MTKey={MTKey} />
                    <Footer />
                </PrivateRoute>
                <div>
                    <Nav setEmail={setEmail} />
                    <Route exact path='/signin'>
                        {authState === null ?
                            <SignIn
                                signInUser={signInUser}
                                signInMsg={signInMsg}
                                setSIMsg={setSIMsg}
                                authState={authState}
                            />
                            : <Redirect to='/dashboard' />
                        }
                    </Route>
                    <Route exact path='/signup'>
                        {authState === null ?
                            <SignUp
                                email={email}
                                createUser={createUser}
                                signUpMsg={signUpMsg}
                                setSUMsg={setSUMsg}
                                authState={authState}
                            />
                            : <Redirect to='/dashboard' />
                        }
                    </Route>
                    <Route exact path='/account'>
                        <Account />
                    </Route>

                    <Footer />
                </div>
            </Switch>
        </BrowserRouter >
    );
};

export default withRouter(Router);
