import { useRef } from 'react';
import { auth } from '../firebase/firebase';
import { useHistory } from 'react-router-dom';
import { customMsg, checkPwMatch } from '../helper/helper';
import AuthMsg from './AuthMsg';
import '../styles/Account.css';

const Account = ({ saveUserName, saveUserEmail, saveUserPassword, displayName, email, acctMsg, setAcctMsg }) => {
    const history = useHistory();

    const nameField = useRef();
    //const emailField = useRef();
    const pw = useRef();
    const confirmpw = useRef();

    function handleSubmit(e) {
        e.preventDefault();
        // if (checkPwMatch(pw.current.value, confirmpw.current.value) === false) {
        //     setAcctMsg(<AuthMsg useClass={`auth-error`} msg={customMsg(`auth/confirm-pw-error`)} />);
        //     return;
        // }
        //saveUserName(auth, nameField.current.value);
        saveUserEmail(auth, nameField.current.value);
        //saveUserPassword(auth, pw.current.value);
    }
    return (
        <div className='acct'>
            <div className='acct-container'>
                <h1>Update Account Info</h1>
                {acctMsg}
                <form onSubmit={(e) => handleSubmit(e)}>
                    <label>First Name</label>
                    <input ref={nameField} type='text' placeholder='Name' defaultValue={displayName} required></input>

                    <label>Password</label>
                    <input ref={pw} type='password' minLength='6' placeholder='Password' required></input>

                    <label>Confirm Password</label>
                    <input ref={confirmpw} type='password' minLength='6' placeholder='Confirm Password' required></input>

                    <input id='acct-submit' type='submit' value='Submit'></input>
                </form>
            </div>
            <div className='overlay'></div>
        </div >
    );
};

export default Account;

