import React, { useEffect, useState } from 'react';
import { BrowserRouter, Route, Switch, Redirect, useHistory, withRouter } from 'react-router-dom';
import SignIn from '../components/SignIn';
import SignUp from '../components/SignUp';
import Nav from '../components/Nav';
import Home from '../components/Home';

const Router = (props) => {
    const [hideSignIn, setHideSignIn] = useState(false);
    console.log(props.location.pathname, 'routerprops');

    useEffect(() => {
        if (props.location.pathname !== '/') {
            setHideSignIn(true);
        } else {
            setHideSignIn(false);
        }
        console.log(hideSignIn, 'abc');
    });



    function handleClick() {
        //history.push('/signin');
    }

    async function submitHandSignIn(e) {
        e.preventDefault();
        console.log('prevented Submit');
    }


    return (
        <BrowserRouter>
            <Switch>
                <Route exact path='/signin' render={() => <SignIn submitHandSignIn={submitHandSignIn} />} />
                <Route exact path='/signup' component={SignUp} />
                <Route exact path='/' render={() => <Home location={props.location} hideSignIn={hideSignIn} />} />
            </Switch>
        </BrowserRouter >
    );
};

export default withRouter(Router);

