import { useHistory } from 'react-router-dom';
import '../styles/Home.css';

const Home = (props) => {
    const { email, setEmail } = props;

    const history = useHistory();

    function routeChange() {
        let path = `signup`;
        history.push(path);
    }

    function handleSubmit(e) {
        e.preventDefault();
        const [input,] = e.target.childNodes;
        console.log(e.target.childNodes, input.value);
        setEmail(input.value);
    }

    return (
        <div className='home'>
            <div className='home-overlay'>
                <div className='home-signUp'>
                    <h1>Unlimited movies, TV shows, and more.</h1>
                    <h2>Watch anywhere. Cancel anytime.</h2>
                    <h3>Ready to watch? Enter your email to create or restart your membership.</h3>
                    <form onSubmit={(e) => handleSubmit(e)}>
                        <input id='home-email' type='email' placeholder='Email address' required></input>
                        <input id='home-submit' type='submit' value='Get Started &#8250;'></input>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Home;
