import { Link, Switch } from 'react-router-dom';
import { collection, addDoc } from 'firebase/firestore';
import db from '../firebase/firebase';
import '../styles/SignUp.css';

const SignUp = (props) => {
    const { email } = props;

    async function handleSubmit(e) {
        e.preventDefault();
        const [firstName, email, pw1, pw2] = e.target.childNodes;
        if (checkPw(pw1, pw2) === false) {
            console.log('passwords do not match');
            return;
        }
        setCursor('wait');
        console.log('pw match');
        addToDoc(firstName, email, pw1);
    }

    function checkPw(pw1, pw2) {
        return pw1.value === pw2.value ? true : false;
    }

    function setCursor(val) {
        document.querySelectorAll('*').forEach((elem) => {
            elem.style.cursor = val;
        });
    }

    async function addToDoc(firstName, email, password) {
        console.log(firstName, email, password);
        // await addDoc(collection(db, 'users'), {
        //     firstName,
        //     email,
        //     password,
        // });
    }

    return (
        <form className='signUp' onSubmit={(e) => handleSubmit(e)} style={{ cursor: 'default' }}>
            <input placeholder='First Name' />
            <input type='email' placeholder='Email Address' defaultValue={email} />
            <input type='password' placeholder='Password' />
            <input type='password' placeholder='Confirm Password' />
            <input type='submit' value='Sign Up' />
            <div>
                Already a user? <Link to='/signin'>Sign in now</Link>
            </div>
        </form>
    )
};

export default SignUp;
