        <div className='active' style={{ background: `url(${image})` }}>
            <div className='pvInfo-container fade-in'>
                <h1>{`${title}`}</h1>
                <p>{`${description}`}</p>
                <PlayButton
                    hoverPlayBtn={hoverPlayBtn}
