import { useRef, useMemo, createRef, useState, createElement } from 'react';
import { capitalizeFirstLetter } from '../helper/helper';
import YoutubeEmbed from './YoutubeEmbed';
import MainMedia from './MainMedia';
import MediaBar from './MediaBar';
import Preview from './Preview';
import films from '../assets/films';
import '../styles/Dashboard.css';

const Dashboard = () => {
    const [currentMedia, setCurrentMedia] = useState(null);
    const [currentPreview, setCurrentPreview] = useState(null);

    function onClickHand(e) {
        const [genre, ,] = e.target.classList;
        const filmName = e.target.alt;
        if (true) {
            const preview = document.querySelector(`#${genre}Preview`); //select preview container based on genre
            setCurrentPreview(<Preview preview={getPreview(genre, filmName)} />);

        } else {
            const catalog = films[genre].find((item) => item.title === filmName);
            setCurrentMedia(<YoutubeEmbed embedId={catalog.YTLink} stopPlayHand={stopPlayHand} />);
        }

        const Preview = (props) => {
            const { preview } = props;
            return (
                <div className='active' style={{ background: `url(${preview})` }}>
                </div>
            )
        };
    }

    function getPreview(genre, filmName) {
        return require(`../assets/films/${genre}/${filmName}/large.jpg`).default;
    }

    function hoverPlayBtn(e) {
        e.target.classList.add('hover');
    }

    function hoverOffPlayBtn(e) {
        e.target.classList.remove('hover');
    }

    function hoverImgHand(e) {
        e.target.classList.add('cardImgHover');
    }

    function hoverOutImgHand(e) {
        e.target.classList.remove('cardImgHover');
    }

    function playBtnClickHand(e, url) {
        setCurrentMedia(<YoutubeEmbed embedId={url} stopPlayHand={stopPlayHand} />);
    }

    function stopPlayHand(e) {
        setCurrentMedia(null);
    }

    function makeMediaBar() {
        let arr = [];
        for (let genre in films) {
            arr.push(
                <div className='media-wrapper'>
                    <div className='media-container'>
                        <div className='media-title'>{capitalizeFirstLetter(genre)}</div>
                        <MediaBar
                            title={genre}
                            onClickHand={onClickHand}
                            hoverImgHand={hoverImgHand}
                            hoverOutImgHand={hoverOutImgHand}
                        />
                        <div id={`${genre}Preview`} className='preview-container'>
                        </div>
                    </div>
                </div>
            );
        }
        return arr.map((item, index) => {
            return (
                <div key={index}>
                    {item}
                </div>
            );
        });
    }

    return (
        <div className='dashboard'>
            <MainMedia
                display={`joker`}
                hoverPlayBtn={hoverPlayBtn}
                hoverOffPlayBtn={hoverOffPlayBtn}
                playBtnClickHand={playBtnClickHand} />
            <div>
                {makeMediaBar()}
            </div>
            {currentMedia}
        </div >
    );
};

export default Dashboard;


