import '../styles/MediaBar.css';
import films from '../assets/films/index.js';
import { useRef } from 'react';
import { capitalizeFirstLetter } from '../helper/helper';

const MediaBar = (props) => {
    const previewText = useRef();
    const { title } = props;

    function hoverHand(e) {
        console.log(previewText.current);
        previewText.current.setAttribute('class', 'display: block');
    }

    function getFilms() {
        for (const key in films) {
            if (title === key) {
                return films[key];
            }
        }
    }

    function makePreviews(arr) {
        return arr.map((item, index) => {
            const img = require(`../assets/films/${title}/${item.title}/small.jpg`);
            return (
                <div onMouseOver={(e) => hoverHand(e)}>
                    <img className='previewImg' key={index} src={img.default} alt='' />
                    <div ref={previewText} className='previewText'>
                        <div className='title'>{item.title}</div>
                        <div>{item.description}</div>
                    </div>
                </div>
            )
        });

    }

    return (
        <div>
            <div className='mediaTitle'>{capitalizeFirstLetter(title)}</div>
            <div className='mediaCont'>
                {makePreviews(getFilms())}
            </div>
        </div>
    )
};

export default MediaBar;
