import { useRef } from 'react';
import MediaBar from './MediaBar';
import films from '../assets/films';
import joker from '../images/joker.jpg';
import '../styles/Dashboard.css';

const Dashboard = () => {
    document.querySelector('html').style.backgroundImage = `url(${joker})`;

    const media = useRef();

    function onClickHand(e) {
        console.log(e.target);
        console.log(media);
    }

    // function makeMediaBar() {
    //     let arr = [];
    //     for (let key in films) {
    //         arr.push(
    //             <MediaBar title={key} />
    //         );
    //     }
    //     return arr.map((item, index) => {
    //         return item;
    //     });
    // }
    return (
        <div className='dashboard' style={{ background: `url(${joker})` }}>
            <div className='mainMediaCont'>
                <div>Watch Joker Now</div>
                <div>
                    Forever alone in a crowd, failed comedian Arthur Fleck
                    seeks connection as he walks the streets of Gotham City.
                    Arthur wears two masks -- the one he paints for his day job
                    as a clown, and the guise he projects in a futile attempt to
                    feel like he's part of the world around him.
                </div>
                <button className='mainPlayBtn'>Play</button>
            </div>
            <div ref={media}>
                <div>
                    <MediaBar title={`children`} onClickHand={onClickHand} />
                    <div id='children'>Hello World</div>
                </div>
                <div>
                    <MediaBar title={`drama`} onClickHand={onClickHand} />
                    <div id='drama'>Hello World</div>
                </div>
                <div>
                    <MediaBar title={`romance`} onClickHand={onClickHand} />
                    <div id='romance'>Hello World</div>
                </div>
                <div>
                    <MediaBar title={`suspense`} onClickHand={onClickHand} />
                    <div id='suspense'>Hello World</div>
                </div>
                <div>
                    <MediaBar title={`thriller`} onClickHand={onClickHand} />
                    <div id='thriller'>Hello World</div>
                </div>
            </div>
        </div >
    );
};

export default Dashboard;
