import '../styles/MediaBar.css';
import films from '../assets/films/index.js';

const MediaBar = (props) => {
    const { title, onClickHand, hoverImgHand, hoverOutImgHand } = props;

    async function makePreviews(arr) {
        return arr.map((item, index) => {
            const img = require(`../assets/films/${title}/${item.title}/small.jpg`);
            return (
                <img
                    key={index}
                    className={`${title} cardImg`}
                    onMouseOver={(e) => hoverImgHand(e)}
                    onMouseOut={(e) => hoverOutImgHand(e)}
                    onClick={(e) => onClickHand(e)}
                    src={img.default}
                    alt={item.title}
                />
            )
        });

    }

    return (
        <div className='cards'>
            {makePreviews(films[title])}
        </div>
    )
};

export default MediaBar;
