import '../styles/Home.css';

const Home = ({ email, setEmail }) => {
    const emailInput = useRef();
    const history = useHistory();

    function redirectSignUp() {
        history.push(`signup`);
    }

    function handleSubmit(e) {
        e.preventDefault();
        const [input,] = e.target.childNodes;
