                        <input ref={emailInput} id='home-email' type='email' placeholder='Email address' required></input>
                        <input id='home-submit' type='submit' value='Get Started &#8250;'></input>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Home;
